import React from 'react';
import { useParams } from 'react-router-dom';
import productData from '../../data/ProductData'; 
import './ProductDetail.css'; 

function ProductDetail() {
  const { productId } = useParams(); 
  const product = productData.find(p => p.id === parseInt(productId));

  console.log(product);

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div className="product-detail wrapper">
      <div className="breadcrumb">
        <a href="/">Home</a> 
        <span>/</span> 
        <a href="/gaming">Gaming</a>
        <span>/</span> 
        <p>{product.name}</p>  
      </div>

      <div className="product-detail-main">
        <div className="product-image">
          <img src={product.image} alt={product.name} />
        </div>

        <div className="products-grid">
    {product.relatedItems && product.relatedItems.length > 0 ? (
        product.relatedItems.map((item) => (
        <div key={item.id} className="product-card">
            <img src={item.image} alt={item.name} />
            <span className="discount">{item.discount}</span>
            <h4>{item.name}</h4>
            <p className="price">
            ${item.price} <span className="old-price">${item.oldPrice}</span>
            </p>
            <img src={item.stars} alt="Stars" className="stars" />
            <p className="rating">({item.rating})</p>
        </div>
    ))
  ) : (
    <p>No related items available</p>
  )}
</div>
  </div>
    </div>
  );
}

export default ProductDetail;
