import React from 'react';
import { useParams } from 'react-router-dom';
import productData from '../../data/ProductData'; 
import './ProductDetail.css'; // Assuming you already have the styles

function ProductDetail() {
  const { productId } = useParams(); // Get the product ID from the URL
  const product = productData.find(p => p.id === parseInt(productId)); // Find the product using the ID

  // If the product isn't found, show a "Product Not Found" message
  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div className="product-detail">
      <div className="breadcrumb">
        <a href="/">Home</a> / <a href="/gaming">Gaming</a> / {product.name}
      </div>

      <div className="product-detail-main">
        <div className="product-image">
          <img src={product.mainImage} alt={product.name} />
        </div>

        <div className="product-info">
          <h1>{product.name}</h1>
          <div className="rating">
            <img src="images/Five star.png" alt="Rating" />
            <p>({product.reviews} Reviews)</p>
          </div>
          <p className="price">${product.price.toFixed(2)}</p>
          <p className={`stock ${product.stockStatus.toLowerCase()}`}>
            {product.stockStatus}
          </p>
          <p className="description">{product.description}</p>

          <div className="colors">
            <h4>Colours:</h4>
            <div className="color-options">
              {product.colors.map((color, index) => (
                <span
                  key={index}
                  style={{ backgroundColor: color }}
                  className="color-circle"
                ></span>
              ))}
            </div>
          </div>

          <div className="delivery-info">
            <p>🚚 {product.delivery}</p>
            <p>🔄 {product.returnPolicy}</p>
          </div>
        </div>
      </div>

      <div className="related-products">
        <h2>Related Items</h2>
        <div className="products-grid">
          {product.relatedItems.map((item) => (
            <div key={item.id} className="product-card">
              <img src={item.image} alt={item.name} />
              <span className="discount">{item.discount}</span>
              <h4>{item.name}</h4>
              <p className="price">
                ${item.price} <span className="old-price">${item.oldPrice}</span>
              </p>
              <img src={item.stars} alt="Stars" className="stars" />
              <p className="rating">({item.rating})</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
